import React, { Component } from 'react'

import MovieItem from './MovieItem.js'
import styled from 'styled-components'

const Container = styled.div`
  background: #2b2e39;
  margin: 0 auto;
  width: 80%;
  max-width: 600px;
  padding: 14px;
  border-radius: 14px;
  margin-top: 14px;
`

const Header = styled.h1`
  color: #fff;
`

class MovieList extends Component {
  static defaultProps = {
      movies: [],
  }
  constructor (props) {
    super(props)
    this.clearList = this.clearList.bind(this)
    this.refreshList = this.refreshList.bind(this)
    this.state = {
        movies: []
    }
  }

  refreshList () {
    fetch('http://127.0.0.1:8000/api/v1/movies/?title=batman')
    .then(response => response.json())
    .then(json => this.setState({movies: json}))
  }

  clearList () {
      this.setState({
          movies: []
      })
  }

  componentDidMount () {
      fetch('http://127.0.0.1:8000/api/v1/movies/?title=batman&year=2005')
      .then(response => response.json())
      .then(json => this.setState({movies: json}))
  }

  render () {
    const { movies } = this.state
    return (
      <Container>
        <Header>Movie list from omdbapi</Header>
        <button onClick={this.clearList}>Clear list</button>
        <label for="title">Title</label>
        <input type='text' name="Title" id="title"/>
        <button>request movies</button>
        {movies.results !== undefined ? movies.results.map(item =>
        <MovieItem
        title={item.Title}
        type={item.Type}
        year={item.Year}
        imdbID={item.imdbID} />) : ''}
      </Container>
    )
  }
}

export default MovieList
