import React, { Component } from 'react'

import styled from 'styled-components'

import * as api from '../api_helper/api'
import { loginUrl } from '../api_helper/routes'

const LoginHeader = styled.h4`
  text-align: center;
  margin-right: 20px;
`
const Container = styled.div`
  background: #2b2e39;
  margin: 0 auto;
  width: 80%;
  max-width: 600px;
  padding: 14px;
  border-radius: 14px;
  margin-top: 14px;
`

class LoginUser extends Component {
  constructor (props) {
    super(props)
    this.handleLogin = this.handleLogin.bind(this)
    this.handleChange = this.handleChange.bind(this)
    this.state = {
      credentials: { username: '', password: '', email: '' },
      key: ''
    }
  }

  async handleLogin (event) {
    const { loginCallback } = this.props
    const user = await api.post(loginUrl(), this.state.credentials)
    if (loginCallback !== undefined) {
      user.key !== undefined ? loginCallback(user.key) : loginCallback('')
    }
  }

  handleChange (event) {
    const cr = this.state.credentials
    cr[event.target.name] = event.target.value
    this.setState({ credentials: cr })
  }

  render () {
    return (
      <Container>
        <LoginHeader>Login</LoginHeader>
        <label>
          Username:
          <input
            type='text'
            name='username'
            value={this.state.credentials.username}
            onChange={this.handleChange}
          />
        </label>
        <br />
        <label>
          Email:
          <input
            type='email'
            name='email'
            value={this.state.credentials.email}
            onChange={this.handleChange}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type='password'
            name='password'
            value={this.state.credentials.password}
            onChange={this.handleChange}
          />
        </label>
        <button onClick={this.handleLogin}>Login</button>
      </Container>
    )
  }
}

export default LoginUser
