import React, { Component } from 'react'
import styled from 'styled-components'

const Button = styled.button`
  background: #232632;
  color: #00a7fa;
  width: 80px;
  height: 32px;
  font-size: 1.7em;
  border: 0px;
  justify-content: center;
  align-items: center;
  &:hover { background: #555; } 
`

const Container = styled.div`
  justify-content: space-between;
  border: 2px solid #343744;
  background: #232632;
  border-radius: 10px;
  padding: 5px;
`

class MovieItem extends Component {
  constructor (props) {
    super(props)
    this.state = {
      title: null,
      year: null,
      type: null,
      imdbID: null
    }
  }

  componentDidMount () {
    const { title, year, type, imdbID } = this.props
    this.setState({
      title: title,
      year: year,
      type: type,
      imdbID: imdbID
    })
  }

  render () {
    const { title, year, type, imdbID } = this.state
    return (
      <Container>
        <p>Movie title: {title}</p>
        <p>Year: {year}</p>
        <h5>Type: {type}</h5>
        <h5>imdbID: {imdbID}</h5>
      </Container>
    )
  }
}

export default MovieItem
