import React, { Component } from 'react'

class MovieItem extends Component {
  constructor (props) {
    super(props)
    this.state = {
      title: null,
      year: null,
      type: null,
      imdbID: null
    }
  }

  componentDidMount () {
    const { movieRecord } = this.props
    this.setState({
      movieItem: movieRecord
    })
  }

  render () {
    const { movieItem } = this.state
    return (
      <div>
        {movieItem !== null ?
          <p>{movieItem.Title}</p> 
          : ''}
      </div>
    )
  }
}

export default MovieItem
