import React, { Component } from 'react'
import './App.css'

import styled from 'styled-components'

import MovieList from './components/MovieList'
import LoginUser from './components/LoginUser'

const UserHeader = styled.h3`
  font-size: 0.8em;
  margin: right;
`

class App extends Component {
  constructor (props) {
    super(props)
    this.onLogginCallback = this.onLogginCallback.bind(this)
    this.state = { reload: false }
  }

  onLogginCallback () {
    this.setState({ reload: true })
  }

  render () {
    return (
      <div>
        <UserHeader>User: {localStorage.username}</UserHeader>
        {localStorage.token !== undefined ? <MovieList /> : <LoginUser logginCallback={this.onLogginCallback} />}
      </div>
    )
  }
}

export default App
